// Map provider IDs to their icon components
export const ProviderIconMap: Record<ProviderId, React.ComponentType<IconProps>> = {
  claude: ClaudeIcon,
  codex: CodexIcon,
  cursor: CursorIcon,
  copilot: CopilotIcon,
  gemini: GeminiIcon,
  jetbrains: JetbrainsIcon,
  vertexai: VertexIcon,
  kiro: KiroIcon,
  opencode: OpencodeIcon,
  factory: FactoryIcon,
  amp: AmpIcon,
  augment: AugmentIcon,
  zai: ZaiIcon,
  minimax: MinimaxIcon,
  kimi: KimiIcon,
  kimi_k2: KimiIcon,
  antigravity: AntigravityIcon,
  synthetic: SyntheticIcon,
};

// Helper component that renders the appropriate icon for a provider
export function ProviderIcon({ providerId, className }: { providerId: ProviderId; className?: string }) {
  const IconComponent = ProviderIconMap[providerId];
  if (!IconComponent) {
    return null;
  }
  return <IconComponent className={className} />;
}

const incidentColorMap: Record<NonNullable<ProviderIconOverlayProps['indicator']>, string> = {
  none: 'bg-[var(--accent-success)]',
  minor: 'bg-[var(--accent-warning)]',
  major: 'bg-[var(--accent-danger)]',
  critical: 'bg-[var(--accent-danger)]',
  maintenance: 'bg-[var(--accent-warning)]',
  unknown: 'bg-[var(--text-quaternary)]',
};

export function ProviderIconWithOverlay({
  className,
  indicator = 'none',
  title,
}: ProviderIconOverlayProps) {
  if (indicator === 'none') {
    return null;
  }
  const label = title ?? `Provider status: ${indicator}`;
  const colorClass = incidentColorMap[indicator] ?? incidentColorMap.unknown;
  return (
    <span
      className={`absolute -top-0.5 -right-0.5 w-2 h-2 rounded-full ring-2 ring-[var(--bg-base)] ${colorClass} ${className ?? ''}`}
      role="status"
      aria-label={label}
      title={label}
    />
  );
}
